  private void catsValueChanged(javax.swing.event.ListSelectionEvent evt) {//GEN-FIRST:event_catsValueChanged
    int idx = cats.getSelectedIndex();
    if (idx == -1) return;
    loadCategory(idx);
  }//GEN-LAST:event_catsValueChanged

